# Word Search
# Backtrack
# Time: O(w * m * n) | Space: O(1)

from typing import List

def exist(board: List[List[str]], word: str) -> bool:
    if word:
        # Iterate through entire board, checking for start of word
        for i in range(len(board)):
            for j in range(len(board[0])):
                # If start of word found, search for rest of word
                if board[i][j] == word[0]:
                    if backtrack(board, i, j, word, 0):
                        return True
    return False

def backtrack(board, i, j, word, k):
    # If indices out of bounds or letter doesn't match
    if k > len(word) - 1 or i < 0 or j < 0 or i > len(board) - 1 or j > len(board[0]) - 1 or board[i][j] != word[k]:
        return False
    # Letter matches
    else:
        # If reached end of word, return True
        if k == len(word) - 1:
            return True
        # Search up, down, left, and right for next letter. Mark that current letter has been used
        board[i][j] = ''
        if backtrack(board, i - 1, j, word, k + 1):
            return True
        if backtrack(board, i, j - 1, word, k + 1):
            return True
        if backtrack(board, i + 1, j, word, k + 1):
            return True
        if backtrack(board, i, j + 1, word, k + 1):
            return True
        board[i][j] = word[k]
    return False
    
